import React from 'react'
import { useState ,useEffect} from 'react'
import CategoryCard from '../Card/CategoryCard'
import "./Categories.scss"
import axios from 'axios'

const Categories = ({type}) => {

    const [ categories, setCategories ] = useState([])
    

    useEffect(()=>{
        const fethCategories = async ()=>{
            try{
                const data = await axios.get('http://localhost:8080/categories')
                setCategories(data.categories.categories)
            }catch(err){
                console.log(err)
            }
        };
        fethCategories();
    },[])

    // const fethCategories = () => {
    //     axios.get('http://localhost:8080/categories',).then(response => {
    //         setCategories(response.data)
    //     })
    // }

    // console.log(categories)
    

    // useEffect(() => {
    //     fethCategories()
    // },[])

    // console.log(categories)

  return (
        <div className='categories'>
            <div className="top">
            <h1>{type}categories</h1>
        </div>
        <div className="bottom">
            {categories.map(category=>(
                <CategoryCard item={category} key={category.id}/>
            ))}
        </div>
    </div>
  )
}


export default Categories
